export function zhToEn(text:string):string{

    var reg = new RegExp("[^ ]*" + text + "[^ ]*");
    var ms = dict.match(reg);
    if (ms==undefined|| ms.length == 0) {
        return undefined;
    }
    var s = ms[0].split(".")[0];
    if (s.endsWith("n")) {
        s = s.substring(0, s.length - 1);
    } else if (s.endsWith("adj")) {
        s = s.substring(0, s.length - 3);
    } else if (s.endsWith("adv")) {
        s = s.substring(0, s.length - 3);
    } else if (s.endsWith("v")) {
        s = s.substring(0, s.length - 1);
    } else if (s.endsWith("v")) {
        s = s.substring(0, s.length - 1);
    }
    return s;

}